import React, {useEffect, useReducer} from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  flex: 1;
`;

const ListContainer = styled.div`
  display: flex;
  flex-direction: column;
  overflow-y: scroll;
  flex-wrap: no-wrap;
`;

const Inside = styled.button`
  width: 100%;
  background-color: white;
`;

interface State {
  loading: boolean,
  data: Data | null,
  error: string | null | unknown
}

interface Data {
  viewAmenitiesInfo : Va,
  stayLodgeInfo : Va,
  touristFoodInfo: Va,
}

interface Va {
  row: string
}

type ReducerProps = 
  | {type: 'LOADING'; }
  | {type: 'SUCCESS'; data: Data | null}
  | {type: 'ERROR'; error: string | null | unknown }


const reducer = (state: State, action: ReducerProps) => {
  switch (action.type) {
    case 'LOADING':
      return {
        loading: true,
        data: null,
        error: null,
      };
    case 'SUCCESS':
      return {
        loading: false,
        data: action.data,
        error: null,
      };
    case 'ERROR':
      return {
        loading: false,
        data: null,
        error: action.error,
      };
    default: 
    throw new Error(`unhandled action`);
  }
}

const viewAmenitiesInfo = 'viewAmenitiesInfo';
const stayLodgeInfo = 'stayLodgeInfo';
const touristFoodInfo = 'touristFoodInfo';

const Side = () => {
  const [state, dispatch] = useReducer(reducer, {
    loading: false,
    data: null,
    error: null
  })

  const fetchUsers = async (a: string, b: number) => {
    dispatch({ type: 'LOADING'});
    try {
      const response = await axios.get(
        `http://openapi.seoul.go.kr:8088/486857594b726863383574794f4669/json/${a}/1/${b}/`
      );
      dispatch({ type: 'SUCCESS', data: response.data});
    } catch (e) {
      dispatch({ type: 'ERROR', error: e})
    }
  }

  useEffect(() => {
    fetchUsers(viewAmenitiesInfo, 5);
    /*fetchUsers(stayLodgeInfo, 5);
    fetchUsers(touristFoodInfo, 5);*/
  }, []);

  let array1 = [];
  let array2 = [];
  let array3 = [];

  if(!state.data) return null;
  if(state.data) {
    for(let i=0; i<5; i++) {
      array1[i] = state.data.viewAmenitiesInfo.row[i];
    }
    for(let i=0; i<5; i++) {
      array2[i] = state.data.stayLodgeInfo.row[i];
    }
    for(let i=0; i<5; i++) {
      array3[i] = state.data.touristFoodInfo.row[i];
    }
  }
  return (
    <Container>
      <ListContainer>
        {array1.map((e: any) => <Inside key={e.SISULNAME}>{e.SISULNAME} ({e.ADDR})</Inside>)}
      </ListContainer>
    </Container>
  )
}

export default Side;
