import React, {useState, useEffect, useReducer} from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  flex: 1;
`;

const ListContainer = styled.div`
  display: flex;
  flex-direction: column;
  overflow-y: scroll;
  flex-wrap: no-wrap;
`;

const Inside = styled.button`
  width: 100%;
  background-color: white;
`;

interface State {
  loading: boolean,
  data: Data | null,
  error: string | null | unknown
}

interface Data {
  viewAmenitiesInfo : Va
}

interface Va {
  row: string
}

type ReducerProps = 
  | {type: 'LOADING'; }
  | {type: 'SUCCESS'; data: Data | null}
  | {type: 'ERROR'; error: string | null }


const reducer = (state: State, action: ReducerProps) => {
  switch (action.type) {
    case 'LOADING':
      return {
        loading: true,
        data: null,
        error: null,
      };
    case 'SUCCESS':
      return {
        loading: false,
        data: action.data,
        error: null,
      };
    case 'ERROR':
      return {
        loading: false,
        data: null,
        error: action.error,
      };
    default: 
    throw new Error(`unhandled action`);
  }
}

const Side = () => {
  const [data, setData] = useState([]);
  const [state, dispatch] = useReducer(reducer, {
    loading: false,
    data: null,
    error: null
  })

  const fetchUsers = async () => {
    dispatch({ type: 'LOADING'});
    try {
      const response = await axios.get(
        `http://openapi.seoul.go.kr:8088/486857594b726863383574794f4669/json/viewAmenitiesInfo/1/5/`
      );
      dispatch({ type: 'SUCCESS', data: response.data});
    } catch (e) {
      dispatch({ type: 'ERROR', error: e})
    }
  }

  useEffect(() => {
    fetchUsers();
  }, []);

  let array = [];

  if(!state.data) return null;
  if(state.data) {
    for(let i=0; i<5; i++) {
      array[i] = state.data.viewAmenitiesInfo.row[i];
    }
  }
  return (
    <Container>
      <ListContainer>
        {array.map((e: any) => <Inside key={e.SISULNAME}>{e.ADDR}</Inside>)}
      </ListContainer>
    </Container>
  )
}

export default Side;
